    def sending(self):
      v1=self.comboBox.currentText()
      v2=self.comboBox_2.currentText()
      led1=self.comboBox_3.currentText()
      if v1=='Alta' and v2=='Alta':
        puertoSerial.write(str.encode('a'))
      elif v1=='Alta' and v2=='Media':
        puertoSerial.write(str.encode('b'))
      elif v1=='Alta' and v2=='Baja':
        puertoSerial.write(str.encode('c'))
      elif v1=='Media' and v2=='Alta':
        puertoSerial.write(str.encode('d'))
      elif v1=='Media' and v2=='Media':
        puertoSerial.write(str.encode('e'))
      elif v1=='Media' and v2=='Baja':
        puertoSerial.write(str.encode('f'))
      elif v1=='Baja' and v2=='Alta':
        puertoSerial.write(str.encode('g'))
      elif v1=='Baja' and v2=='Media':
        puertoSerial.write(str.encode('h'))
      elif v1=='Baja' and v2=='Baja':
        puertoSerial.write(str.encode('i'))
      time.sleep(3)
      #-----------------------------------
      if led1=='Modo 1':
        puertoSerial.write(str.encode('j'))
      elif led1=='Modo 2':
        puertoSerial.write(str.encode('k'))
      elif led1=='Modo 3':
        puertoSerial.write(str.encode('l'))
      elif led1=='Modo 4':
        puertoSerial.write(str.encode('m'))
      time.sleep(3)
